import React, { useState, useEffect } from 'react';

export default function SuperheroNameGenerator() {
  const [superheroName, setSuperheroName] = useState('');

  useEffect(() => {
    fetch('URL_DE_VOTRE_API_DE_GENERATION_DE_NOMS')
      .then(response => response.json())
      .then(data => setSuperheroName(data.superheroName))
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <h1>Générateur de Noms de Super-héros</h1>
      <p>Nom du Super-héros : {superheroName}</p>
      <button onClick={() => generateSuperheroName()}>Générer un nouveau nom</button>
    </div>
  );

  function generateSuperheroName() {
    fetch('URL_DE_VOTRE_API_DE_GENERATION_DE_NOMS')
      .then(response => response.json())
      .then(data => setSuperheroName(data.superheroName))
      .catch(error => console.error(error));
  }
}
