import React, { useState, useEffect } from 'react';

export default function SuperheroNameGenerator() {
  const [superheroName, setSuperheroName] = useState('');

  useEffect(() => {
    async function fetchSuperheroName() {
      try {
        const response = await fetch('URL_DE_VOTRE_API_DE_GENERATION_DE_NOMS');
        if (response.ok) {
          const data = await response.json();
          setSuperheroName(data.superheroName);
        } else {
          console.error('La requête a échoué.');
        }
      } catch (error) {
        console.error('Une erreur s\'est produite : ', error);
      }
    }
    fetchSuperheroName();
  }, []);

  function generateSuperheroName() {
    fetch('URL_DE_VOTRE_API_DE_GENERATION_DE_NOMS')
      .then(response => response.json())
      .then(data => setSuperheroName(data.superheroName))
      .catch(error => console.error(error));
  }

  return (
    <div>
      <p>Nom du Super-héros : {superheroName}</p>
      <button onClick={generateSuperheroName}>Générer un nouveau nom</button>
    </div>
  );
}
