import React, { useState, useEffect } from 'react';
const express = require('express');
const app = express();
const port = 3000;

export default function SuperheroNameGenerator() {
  const [superheroName, setSuperheroName] = useState('');

  useEffect(() => {
    app.get('/generate-superhero-name', (req, res) => {
        const superheroNames = ["Captain Câlinou", "Mme. Éclat de Rire", "Super Chocolatine", "Captain Tofu", "La Foudre Rose"];
        const randomName = superheroNames[Math.floor(Math.random() * superheroNames.length)];
        res.json({ superheroName: randomName });
      });
  }, []);

  return (
    <div>
      <p>Nom du Super-héros : {superheroName}</p>
      <button onClick={() => generateSuperheroName()}>Générer un nouveau nom</button>
    </div>
  );

  function generateSuperheroName() {
    app.get('/generate-superhero-name', (req, res) => {
        const superheroNames = ["Captain Câlinou", "Mme. Éclat de Rire", "Super Chocolatine", "Captain Tofu", "La Foudre Rose"];
        const randomName = superheroNames[Math.floor(Math.random() * superheroNames.length)];
        res.json({ superheroName: randomName });
      });
  }
}



