import React, { useState } from "react";
import FormComponent from "../Formulaire/formulaire";

export default function Form() {
  const [Memes, setMemes] = useState([]);
  const apiUrl = "https://api.imgflip.com/get_memes";
  const [Image, setImage] = useState({
    textHaut: "",
    textBas: "",
    image: "https://i.imgflip.com/145qvv.jpg",
  });

  React.useEffect(() => {
    async function fetchData() {
      try {
        const res = await fetch(apiUrl);
        if (res.ok) {
          const data = await res.json();
          setMemes(data.data.memes);
        } else {
          console.error('La requête a échoué.');
        }
      } catch (error) {
        console.error('Une erreur s\'est produite : ', error);
      }
    }
    fetchData();
  }, []);

//Choisit un element au hasard depuis le tableau Memes
  function getImage() {
      return Memes[Math.floor(Math.random() * Memes.length)].url;
  }


  function changeMeme(event) {
    const { name, value } = event.target;
    setImage((prevState) => ({
      ...prevState,
      [name]: value,
    }));
    // console.log(meme);
  }
  function handleSubmit(event) {
    event.preventDefault();
    // console.log(Image);
    setImage((prevState) => ({
      ...prevState,
      image: getImage(),
    }));
  }

  return (
    <main>
      <FormComponent Image={Image} changeMeme={changeMeme} handleSubmit={handleSubmit} />

      <div className="meme">
        <img src={Image.image} alt="meme" className="meme--image" />
        <h1 className="meme--text top">{Image.textHaut}</h1>
        <h1 className="meme--text bottom">{Image.textBas}</h1>
      </div>
    </main>
  );
}
